        }
    }, [campaignContract]);
  

    async function connectWallet() {
        if (!window.ethereum) {
            alert("‚ö†Ô∏è Please install MetaMask!");
            return;
        }
        try {
            const web3Provider = new ethers.providers.Web3Provider(window.ethereum);
            await window.ethereum.request({ method: "eth_requestAccounts" });
    
            const signer = web3Provider.getSigner();
            const address = await signer.getAddress();
            const balance = await web3Provider.getBalance(address);
    
            setProvider(web3Provider);
            setAccountAddress(address);
            setAccountBalance(ethers.utils.formatEther(balance));
            setIsLoggedIn(true);
    
            initializeContracts(web3Provider, address);
            fetchBalance(address);
            fetchTokenBalance();
            fetchUserNFTs();
    
            // üîπ EliminƒÉ evenimentele existente √Ænainte de a le adƒÉuga din nou
            window.ethereum.removeListener("accountsChanged", handleAccountChange);
            window.ethereum.removeListener("chainChanged", handleNetworkChange);
    
            // üîπ AdaugƒÉ noile evenimente
            window.ethereum.on("accountsChanged", handleAccountChange);
            window.ethereum.on("chainChanged", handleNetworkChange);
    
        } catch (error) {
            console.error("‚ùå Error connecting to wallet:", error);
            alert("‚ùå Connection failed. Try again.");
        }
    }
    
    // üîπ Definim func»õiile separat pentru a le putea adƒÉuga »ôi elimina corect
    const handleAccountChange = (accounts) => {
        if (accounts.length > 0) {
            setAccountAddress(accounts[0]);
            fetchBalance(accounts[0]);
            fetchUserNFTs();
        } else {
            handleLogout();
        }
    };
    
    const handleNetworkChange = () => {
        alert("üîÑ Network changed! Please reconnect.");
        handleLogout();
    };
  
    async function handleLogout() {
        try {
            setIsLoggedIn(false);
            setAccountAddress('');
            setAccountBalance('');
            setProvider(null);
            setCampaignContract(null);
            setDonationTokenContract(null);
            setProposalContract(null);
            setTotalDonations(0);
            setTargetAmount('');
            setProgress(0);
            setIsTargetReached(false);
            setIsContractsLoaded(false);
            setIsOwner(false);
            setUserTokens([]);
            setUserVotes({});
            setDonationPercentages([]);
    
            // üîπ EliminƒÉ event listenerii corect
            if (window.ethereum) {
                window.ethereum.removeListener("accountsChanged", handleAccountChange);
                window.ethereum.removeListener("chainChanged", handleNetworkChange);
            }
    
            alert("‚úÖ Successfully logged out!");
        } catch (error) {
            console.error("Logout error:", error);
        }
    }
  
  
    useEffect(() => {
        async function checkConnection() {
            if(!window.ethereum || campaignContract) return; 
            if (window.ethereum) {
                try {
                    const accounts = await window.ethereum.request({ method: "eth_accounts" });
                    if (accounts.length > 0) {
                        const web3Provider = new ethers.providers.Web3Provider(window.ethereum);
                        setProvider(web3Provider);
                        setAccountAddress(accounts[0]);
                        fetchBalance(accounts[0]);
                        initializeContracts(web3Provider, accounts[0]);
                        fetchUserNFTs();
                        setIsLoggedIn(true);
                    }
                } catch (error) {
                    console.error("Error checking wallet connection:", error);
                }
            }
        }
        checkConnection();
    }, []);

    async function donate() {
        try {
            if (!isContractsLoaded || !campaignContract) {
                alert("Contracts are still loading. Please wait.");
                return;
            }
    
            if (!ethers.utils.isAddress(accountAddress)) {
                alert("Invalid account address.");
                return;
            }
    
            if (!donationAmount || parseFloat(donationAmount) <= 0) {
                alert("Please enter a valid donation amount.");
                return;
            }
    
            const donationValue = ethers.utils.parseEther(donationAmount);
    
            // Check if the owner is trying to make a donation
            const contractOwner = await campaignContract.owner();
            console.log("Contract owner:", contractOwner);
            console.log("User trying to donate:", accountAddress);
    
            if (accountAddress?.toLowerCase() === contractOwner.toLowerCase()) {
                alert("The owner cannot make donations.");
                return;
            }
    
            console.log("Attempting donation of:", ethers.utils.formatEther(donationValue), "ETH");
    
            // Send the donation transaction
            const donationTx = await campaignContract.donate({ value: donationValue });
    
            console.log("Transaction sent:", donationTx.hash);
    
            // Wait for the transaction to be confirmed
            await donationTx.wait();
            console.log("Transaction confirmed:", donationTx.hash);
            await fetchCampaignData();
            
            fetchTokenBalance();
            fetchDonationPercentages();
    
            setAccountBalance(ethers.utils.formatEther(await provider.getBalance(accountAddress)));
            setTotalDonations(ethers.utils.formatEther(await campaignContract.totalDonations()));
            setTargetAmount(ethers.utils.formatEther(await campaignContract.targetAmount()));
    
            setDonationAmount(""); 
            setShowThankYouPopup(true);
            fetchProposals();
    
            console.log("‚úÖ Donation successful!");
        } catch (error) {
            console.error("‚ùå Error during donation:", error);
    
            if (error.code === "INSUFFICIENT_FUNDS") {
                alert("‚ùå Insufficient funds to complete this transaction.");
            } else if (error.code === "UNPREDICTABLE_GAS_LIMIT") {
                alert("‚ö†Ô∏è Transaction might fail. Possible causes:\n1Ô∏è‚É£ Owner restriction in contract\n2Ô∏è‚É£ Contract is not properly deployed\n3Ô∏è‚É£ Network error.");
            } else {
                alert("‚ö†Ô∏è An unexpected error occurred. Please try again later.");
            }
        }
    }  

    async function setCampaignTarget() {
        try {
