    async function checkUserVotes() {
        if (!proposalContract || !accountAddress) return;
    
        let votes = {};
        for (let i = 0; i < proposals.length; i++) {
            const hasVoted = await proposalContract.hasVoted(proposals[i].id, accountAddress);
            votes[proposals[i].id] = hasVoted;
        }
        setUserVotes(votes);
    }

