    async function handleVote(proposalId, tokenId) {
      try {
          if (!proposalContract) {
              console.warn("Proposal contract not loaded.");
              return;
          }

          if (!tokenId) {
              alert("‚ùå You must select an NFT to vote.");
              return;
          }

          if (userTokens.length === 0) {
              alert("‚ùå You need at least one DONATE NFT to vote.");
              return;
          }

          if (userVotes[proposalId]) {
              alert("‚ùå You have already voted for this proposal.");
              return;
          }


          const voteTx = await proposalContract.vote(proposalId, tokenId);
          await voteTx.wait();

          setUserVotes((prevVotes) => ({
              ...prevVotes,
              [proposalId]: true,
          }));

          fetchTokenBalance();
          fetchDonationPercentages();
          fetchUserNFTs();

          if (totalDonations > 0 && userTokens.length > 0) {
              const availableTokens = userTokens.filter(tokenId => 
                  !Object.values(userVotes).includes(tokenId)
              );
          
              if (availableTokens.length > 0) {
                  setShowVotePopup(true);
              } else {
                  alert("‚ùå All your DONATE NFTs have been used for voting.");
              }
          } else {
              alert("‚ùå You need to donate first and own at least one DONATE NFT to vote.");
          }
          alert("‚úÖ Vote successful!");
      } catch (error) {
          console.error("Error during voting:", error);

          if (error.code === "UNPREDICTABLE_GAS_LIMIT") {
              alert("‚ö†Ô∏è Transaction might fail due to gas estimation issues.");
          } else if (error.code === "CALL_EXCEPTION") {
              alert("‚ùå You may not have permission to vote with this NFT.");
          } else {
              alert("‚ùå An unexpected error occurred. Check the console for details.");
          }
      }
  }

  async function distributeFunds() {
