import React, { useState } from 'react';
import { Web3Storage } from 'web3.storage';

const FileUpload = () => {
  const [file, setFile] = useState(null);
  const [status, setStatus] = useState('');
  const [cid, setCid] = useState('');
  const [token, setToken] = useState('');
  
  const handleFileChange = (event) => {
    setFile(event.target.files[0]);
  };

  const handleTokenChange = (event) => {
    setToken(event.target.value);
  };

  const initializeClient = () => {
    if (!token) {
      setStatus('Please enter a valid API token.');
      return;
    }
    setStatus('Client initialized.');
  };

  const uploadFile = async () => {
    if (!file) {
      setStatus('Please select a file to upload.');
      return;
    }

    try {
      setStatus('Uploading file...');
      
      const client = new Web3Storage({ token }); // Initialize Web3.Storage client with API token
      const fileCid = await client.put([file]); // Upload the file
      
      setCid(fileCid); // Store the CID of the uploaded file
      setStatus(`File uploaded successfully! CID: ${fileCid}`);
    } catch (error) {
      console.error('Error uploading file:', error);
      setStatus('Error uploading file.');
    }
  };

  return (
    <div>
      <h2>Upload File to Web3.Storage</h2>

      <div>
        <input
          type="text"
          placeholder="Enter your Web3.Storage API Token"
          value={token}
          onChange={handleTokenChange}
        />
        <button onClick={initializeClient}>Initialize Client</button>
      </div>

      <div>
        <input type="file" onChange={handleFileChange} />
        <button onClick={uploadFile}>Upload File</button>
      </div>

      <p>{status}</p>
      {cid && <p>File CID: {cid}</p>}
    </div>
  );
};

export default FileUpload;
