import React, { useState } from 'react';
import { Web3Storage } from 'web3.storage';

const UploadFile = () => {
  const [file, setFile] = useState(null);
  const [status, setStatus] = useState('');

  const API_TOKEN = 'z6MkkipjA1gWSEpAerXN91c1nwqjfeeJ5aetK8hpfYVwyKgy';

  // Create Web3.Storage client
  const client = new Web3Storage({ token: API_TOKEN });

  const handleFileChange = (event) => {
    setFile(event.target.files[0]);
  };

  const uploadFile = async () => {
    if (!file) {
      alert('Please select a file!');
      return;
    }

    setStatus('Uploading...');

    try {
      // Upload the file to Web3.Storage
      const cid = await client.put([file]);

      console.log('File uploaded successfully! CID:', cid);

      // Show success message and CID
      setStatus(`File uploaded successfully! CID: ${cid}`);
    } catch (error) {
      console.error('Error uploading file:', error);
      setStatus('File upload failed. Please try again.');
    }
  };

  return (
    <div>
      <h2>Upload a File to Web3.Storage</h2>
      
      {/* File input */}
      <input type="file" onChange={handleFileChange} />
      
      {/* Upload button */}
      <button onClick={uploadFile}>Upload</button>
      
      {/* Status message */}
      <p>{status}</p>
    </div>
  );
};

export default UploadFile;
