import React, { useEffect, useState } from 'react';
import { ethers } from 'ethers';
import campaignAbi from './Campaign.json';  // Path to your Campaign ABI file
import tokenAbi from './DonationToken.json';  // Path to your DonationToken ABI file
import Login from './Login';
import './App.css';

function CampaignInteraction() {
  const [accountAddress, setAccountAddress] = useState(null);
  const [campaignContract, setCampaignContract] = useState(null);
  const [tokenContract, setTokenContract] = useState(null);
  const [signer, setSigner] = useState(null);
  const [provider, setProvider] = useState(null);
  const [campaignProgress, setCampaignProgress] = useState([0, 0, 0]);

  const campaignAddress = '0xe7f1725E7734CE288F8367e1Bb143E90bb3F0512';  // Replace with your actual campaign contract address
  const tokenAddress = '0xYourTokenAddressHere';  // Replace with your actual token contract address

  useEffect(() => {
    // Check if Ethereum is available
    if (window.ethereum) {
      const _provider = new ethers.providers.Web3Provider(window.ethereum);
      setProvider(_provider);

      // Request account access if needed
      window.ethereum.request({ method: 'eth_requestAccounts' }).then(accounts => {
        setAccountAddress(accounts[0]);
        const _signer = _provider.getSigner();
        setSigner(_signer);

        const token = new ethers.Contract(tokenAddress, tokenAbi, _signer);
        setTokenContract(token); 
                const campaign = new ethers.Contract(campaignAddress, campaignAbi, _signer);
        setCampaignContract(campaign);

        // Initialize token contract
      });
    }
  }, []);

  const handleDonate = async (amount) => {
    if (!campaignContract) return;
    try {
      const donationTx = await campaignContract.donate(1, "Donation Purpose", {
        value: ethers.utils.parseEther(amount)
      });
      await donationTx.wait();
      alert('Donation Successful!');
    } catch (error) {
      console.error('Donation failed:', error);
      alert('Donation failed: ' + error.message);
    }
  };

  const handleBuyToken = async (tokenId, amount) => {
    if (!campaignContract || !tokenContract) return;
    try {
      const buyTx = await campaignContract.buyToken(tokenId, {
        value: ethers.utils.parseEther(amount)
      });
      await buyTx.wait();
      alert('Token Purchase Successful!');
    } catch (error) {
      console.error('Token purchase failed:', error);
      alert('Token purchase failed: ' + error.message);
    }
  };

  const getCampaignProgress = async () => {
    if (!campaignContract) return;
    try {
      const progress = await campaignContract.getCampaignProgress();
      setCampaignProgress(progress);
    } catch (error) {
      console.error('Error fetching progress:', error);
    }
  };

  return (
    <div className="App">
      <Login setAccountAddress={setAccountAddress} accountAddress={accountAddress} />
      {accountAddress && (
        <div>
          <h1>Campaign Interaction</h1>
          <p>Account: {accountAddress}</p>

          <div>
            <h2>Donate</h2>
            <input type="number" placeholder="Amount (ETH)" id="donationAmount" />
            <button onClick={() => handleDonate(document.getElementById('donationAmount').value)}>
              Donate
            </button>
          </div>

          <div>
            <h2>Buy Token</h2>
            <input type="number" placeholder="Token ID" id="tokenId" />
            <input type="number" placeholder="Amount (ETH)" id="tokenAmount" />
            <button onClick={() => handleBuyToken(document.getElementById('tokenId').value, document.getElementById('tokenAmount').value)}>
              Buy Token
            </button>
          </div>

          <div>
            <h2>Campaign Progress</h2>
            <button onClick={getCampaignProgress}>Get Progress</button>
            <p>Total Donations: {ethers.utils.formatEther(campaignProgress[0])} ETH</p>
            <p>Current Goal: {ethers.utils.formatEther(campaignProgress[1])} ETH</p>
            <p>Remaining: {ethers.utils.formatEther(campaignProgress[2])} ETH</p>
          </div>
        </div>
      )}
    </div>
  );
}

export default CampaignInteraction;
