import React, { useEffect, useState } from 'react';
import { ethers } from 'ethers';
import campaignAbi from './Campaign.json';
import donationTokenAbi from './DonationToken.json'; // Import the DonationToken ABI
import Login from './Login';
import './App.css';

function CampaignInteraction() {
    console.log("Campaign ABI: ", campaignAbi);
    const [accountAddress, setAccountAddress] = useState('');
    const [accountBalance, setAccountBalance] = useState('');
    const [donationAmount, setDonationAmount] = useState('');
    const [targetAmount, setTargetAmount] = useState('');
    const [progress, setProgress] = useState(0);
    const [totalDonations, setTotalDonations] = useState(0);
    const [provider] = useState(new ethers.providers.JsonRpcProvider("http://localhost:8545"));
    const [signerIndex, setSignerIndex] = useState(null);

    const [campaignContract, setCampaignContract] = useState(null); // State for Campaign contract
    const [donationTokenContract, setDonationTokenContract] = useState(null); // State for DonationToken contract
    const [accountData, setAccountData] = useState([]); // Initialize as empty array
    const [isLoggedIn, setIsLoggedIn] = useState(false);

    useEffect(() => {
        async function fetchAccountData() {
            try {
                const accounts = await provider.listAccounts();
                setAccountData(accounts || []); // Ensure it's always an array
            } catch (error) {
                console.error('Error fetching account data:', error);
            }
        }

        fetchAccountData();
    }, [provider]);

    useEffect(() => {
        async function initializeContracts() {
            if (signerIndex !== null && provider) {
                const signer = provider.getSigner(signerIndex);
    
                // Initialize Campaign Contract
                if (!campaignContract) {
                    const campaignAddress = "0xe7f1725E7734CE288F8367e1Bb143E90bb3F0512"; // Replace with actual deployed contract address
                    try {
                        const campaign = new ethers.Contract(campaignAddress, campaignAbi.abi, signer);
                        setCampaignContract(campaign);
                        console.log("Campaign contract initialized:", campaign);
                    } catch (error) {
                        console.error("Error initializing campaign contract:", error);
                    }
                }
    
                // Initialize Donation Token Contract
                if (!donationTokenContract) {
                    const donationTokenAddress = "0x5FbDB2315678afecb367f032d93F642f64180aa3"; // Replace with actual DonationToken contract address
                    try {
                        const donationToken = new ethers.Contract(donationTokenAddress, donationTokenAbi.abi, signer);
                        setDonationTokenContract(donationToken);
                        console.log("Donation token contract initialized:", donationToken);
                    } catch (error) {
                        console.error("Error initializing donation token contract:", error);
                    }
                }
            }
        }
    
        initializeContracts();
    }, [signerIndex, provider, campaignContract, donationTokenContract]);
    
    // Function to handle login
    async function handleLogin(address) {
        try {
            setIsLoggedIn(true);
            setAccountAddress(address);
            const balance = await provider.getBalance(address);
            setAccountBalance(ethers.utils.formatEther(balance));
    
            // Make sure accountData contains the address you're logging in with
            const index = accountData.indexOf(address);
            if (index === -1) {
                // If address isn't in accountData, handle the error or add it
                console.log("Address not found in accountData.");
            } else {
                setSignerIndex(index);  // Set the signer index after successful login
            }
        } catch (error) {
            console.error('Error during login:', error);
        }
    }
    

    async function donate() {
        try {
            if (!ethers.utils.isAddress(accountAddress)) {
                throw new Error("Invalid account address.");
            }
            if (!campaignContract || !ethers.utils.isAddress(campaignContract.address)) {
                throw new Error("Campaign contract not properly initialized.");
            }
    
            if (donationAmount && parseFloat(donationAmount) > 0) {
                const donationValue = ethers.utils.parseEther(donationAmount);
    
                const balance = await provider.getBalance(accountAddress);
                if (balance.lt(donationValue)) {
                    alert("Insufficient balance for the donation.");
                    return;
                }
    
                console.log("Sending donation...");
                const donationTx = await campaignContract.donate(1, { value: donationValue });
                await donationTx.wait();
                console.log("Donation successful!");
    
                // Update UI state here...
                const updatedBalance = await provider.getBalance(accountAddress);
                setAccountBalance(ethers.utils.formatEther(updatedBalance));
    
                const currentTotalDonations = await campaignContract.totalDonations();
                const currentTargetAmount = await campaignContract.targetAmount();
    
                setTotalDonations(ethers.utils.formatEther(currentTotalDonations));
                setTargetAmount(ethers.utils.formatEther(currentTargetAmount));
    
                const currentProgress = (parseFloat(currentTotalDonations) * 100) / parseFloat(currentTargetAmount);
                setProgress(currentProgress);
            } else {
                alert("Please enter a valid donation amount.");
            }
        } catch (error) {
            console.error('Error making donation:', error);
        }
    }
    

    // Function to set the campaign target amount (only by owner)
    async function setCampaignTarget() {
        try {
            if (campaignContract && parseFloat(targetAmount) > 0) {
                const targetValue = ethers.utils.parseEther(targetAmount);

                // Ensure the target value is higher than the total donations
                const currentTotalDonations = await campaignContract.totalDonations();
                if (targetValue.lte(currentTotalDonations)) {
                    alert("Target amount must be greater than total donations.");
                    return;
                }

                const setTargetTx = await campaignContract.setTargetAmount(targetValue);
                await setTargetTx.wait();

                console.log("Target amount set successfully!");
            } else {
                alert("Please enter a valid target amount.");
            }
        } catch (error) {
            console.error('Error setting target amount:', error);
        }
    }

    return (
        <div>
            {!isLoggedIn ? (
                <Login accountData={accountData} onLogin={handleLogin} />
            ) : (
                <div>
                    <h1>Campaign Interaction</h1>

                    <div>
                        <h2>Connected Account Information</h2>
                        <p>Address: {accountAddress}</p>
                        <p>Balance: {accountBalance} ETH</p>
                    </div>

                    <h2>Campaign Progress</h2>
                    <p>Total Donations: {totalDonations} ETH</p>
                    <p>Target Amount: {targetAmount} ETH</p>
                    <p>Progress: {progress.toFixed(2)}%</p>

                    <hr />

                    <h2>Donate to the Campaign</h2>
                    <label>Donation Amount (ETH or Tokens):</label>
                    <input type="number" value={donationAmount} onChange={(e) => setDonationAmount(e.target.value)} /><br />
                    <button onClick={donate}>Donate</button>

                    <hr />

                    <h2>Set Campaign Target (Owner only)</h2>
                    <label>Target Amount (ETH):</label>
                    <input type="number" value={targetAmount} onChange={(e) => setTargetAmount(e.target.value)} /><br />
                    <button onClick={setCampaignTarget}>Set Target</button>
                </div>
            )}
        </div>
    );
}

export default CampaignInteraction;
