// Import necessary libraries
import React, { useState } from 'react';

// Define the component
function RealEstateTokenInteraction() {
    // State variables to store input values
    const [tokenId, setTokenId] = useState('');
    const [location, setLocation] = useState('');
    const [price, setPrice] = useState('');
    const [recipientAddress, setRecipientAddress] = useState('');

    // Function to handle issuing a new token
    async function handleIssueToken() {
        try {
            await axios.post('/issue-token', { tokenId, location, price });
            alert('Token issued successfully!');
        } catch (error) {
            console.error(error);
            alert('Failed to issue token.');
        }
    }

    // Function to handle transferring a token
    async function handleTransferToken() {
        try {
            await axios.post('/transfer-token', { tokenId, recipientAddress });
            alert('Token transferred successfully!');
        } catch (error) {
            console.error(error);
            alert('Failed to transfer token.');
        }
    }

    // Render the component
    return (
        <div>
            <h1>Real Estate Token Interaction</h1>

            <h2>Issue New Token</h2>
            <label>Token ID:</label>
            <input type="number" value={tokenId} onChange={(e) => setTokenId(e.target.value)} /><br />
            <label>Location:</label>
            <input type="text" value={location} onChange={(e) => setLocation(e.target.value)} /><br />
            <label>Price (ETH):</label>
            <input type="number" value={price} onChange={(e) => setPrice(e.target.value)} /><br />
            <button onClick={handleIssueToken}>Issue Token</button>

            <hr />

            <h2>Transfer Token</h2>
            <label>Token ID:</label>
            <input type="number" value={tokenId} onChange={(e) => setTokenId(e.target.value)} /><br />
            <label>Recipient Address:</label>
            <input type="text" value={recipientAddress} onChange={(e) => setRecipientAddress(e.target.value)} /><br />
            <button onClick={handleTransferToken}>Transfer Token</button>
        </div>
    );
}

// Export the component
export default RealEstateTokenInteraction;
