import React, { useState } from 'react';
import { create } from '@web3-storage/w3up-client';

const UploadFile = () => {
  const [file, setFile] = useState(null);
  const [status, setStatus] = useState('');
  const [client, setClient] = useState(null);  // Store Web3.Storage client

  // Initialize Web3.Storage client
  const initializeClient = async () => {
    const newClient = await create();  // Create client instance
    setClient(newClient);
  };

  // Handle file input change
  const handleFileChange = (event) => {
    setFile(event.target.files[0]);  // Store selected file
  };

  // Upload file to Web3.Storage
  const uploadFile = async () => {
    if (!file || !client) {
      alert('Please select a file and make sure the client is initialized!');
      return;
    }

    setStatus('Uploading...');

    try {
      // Upload the file to Web3.Storage
      const fileCid = await client.uploadFile(file);

      console.log('File uploaded successfully! CID:', fileCid);

      setStatus(`File uploaded successfully! CID: ${fileCid}`);
    } catch (error) {
      console.error('Error uploading file:', error);
      setStatus('Error uploading file. Please try again.');
    }
  };

  // Ensure client is initialized when component mounts
  React.useEffect(() => {
    initializeClient();
  }, []);

  return (
    <div>
      <h2>Upload a File to Web3.Storage</h2>

      {/* File input */}
      <input type="file" onChange={handleFileChange} />

      {/* Upload button */}
      <button onClick={uploadFile}>Upload</button>

      {/* Status message */}
      <p>{status}</p>
    </div>
  );
};

export default UploadFile;
