// Import necessary libraries
import React, { useState } from 'react';
import './App.css';


// Define the component
function RealEstateTokenInteraction() {
    // State variables to store input values
    const [tokenId, setTokenId] = useState('');
    const [location, setLocation] = useState('');
    const [price, setPrice] = useState('');
    const [recipientAddress, setRecipientAddress] = useState('');

    // Function to handle issuing a new token
    async function handleIssueToken() {
    }

    // Function to handle transferring a token
    async function handleTransferToken() {
    }

    async function interact() {
      [owner, user1] = await ethers.getSigners();

      // Replace with your deployed contract address
      let deployedTokenAddress = "0x5FbDB2315678afecb367f032d93F642f64180aa3";
      let token = await ethers.getContractAt("RealEstateToken", deployedTokenAddress);


      // Issue a new token
      let tokenId = 23; // Choose a unique token ID
      let location = "Paris"; // Location of the real estate asset
      let price = ethers.utils.parseEther("100000"); // Price of the real estate asset
      let issueTx=await token.issueToken(tokenId, location, price);

      await issueTx.wait();



      // Call some methods from the token after issuing a new token
      let ownerTokenCountAfter = await token.ownerTokenCount(owner.address);
      console.log("Owner token count after issuing new token:", ownerTokenCountAfter.toString());

      // View information about the issued token
      console.log(owner.address)
      let tokenInfo = await token.viewToken(tokenId);
      console.log("Issued Token Information:");
      console.log("ID:", tokenInfo[0].toString());
      console.log("Location:", tokenInfo[1]);
      console.log("Price:", tokenInfo[2].toString());
      console.log("Owner:", tokenInfo[3]);

      console.log("user address:", user1.address);

      let transferTx = await token.transferToken(user1.address, tokenId);
      await transferTx.wait();
      console.log("Token transferred to user1");

      // Verify the new owner of the token
      tokenInfo = await token.viewToken(tokenId);
      console.log("Updated Token Information:");
      console.log("Owner:", tokenInfo[3]);
  }
    // Render the component
    return (
        <div>
            <h1>Real Estate Token Interaction</h1>

            <h2>Issue New Token</h2>
            <label>Token ID:</label>
            <input type="number" value={tokenId} onChange={(e) => setTokenId(e.target.value)} /><br />
            <label>Location:</label>
            <input type="text" value={location} onChange={(e) => setLocation(e.target.value)} /><br />
            <label>Price (ETH):</label>
            <input type="number" value={price} onChange={(e) => setPrice(e.target.value)} /><br />
            <button onClick={interact}>Issue Token</button>

            <hr />

            <h2>Transfer Token</h2>
            <label>Token ID:</label>
            <input type="number" value={tokenId} onChange={(e) => setTokenId(e.target.value)} /><br />
            <label>Recipient Address:</label>
            <input type="text" value={recipientAddress} onChange={(e) => setRecipientAddress(e.target.value)} /><br />
            <button onClick={handleTransferToken}>Transfer Token</button>
        </div>
    );
}

// Export the component
export default RealEstateTokenInteraction;
