import React, { useState } from 'react';
import { create } from 'ipfs-http-client';

// Infura Project ID
const infuraProjectId = '033c4f3be5d84ba8af1eb00d78434c9d';

// Create IPFS client using Infura and the Project ID
const client = create({
  url: 'https://ipfs.infura.io:5001/api/v0', // IPFS endpoint
  headers: {
    Authorization: `Basic ${btoa(`${infuraProjectId}:`)}` // Encode Project ID with Base64 for authorization
  }
});

const UploadFile = ({ onFileUploaded }) => {
  const [file, setFile] = useState(null);
  const [image, setImage] = useState(null);
  const [link, setLink] = useState('');

  const handleFileChange = (event) => {
    setFile(event.target.files[0]);
  };

  const handleImageChange = (event) => {
    setImage(event.target.files[0]);
  };

  const handleLinkChange = (event) => {
    setLink(event.target.value);
  };

  const uploadToIPFS = async () => {
    try {
      // Check if all fields are filled
      if (!file || !image || !link) {
        alert('Please fill in all fields!');
        return;
      }

      // Upload file to IPFS
      const fileAdded = await client.add(file);
      const imageAdded = await client.add(image);

      console.log('File uploaded to IPFS with CID:', fileAdded.path);
      console.log('Image uploaded to IPFS with CID:', imageAdded.path);

      // Call the onFileUploaded function to pass the CID and link to the parent component
      onFileUploaded(imageAdded.path, fileAdded.path, link);
    } catch (err) {
      console.error('Error uploading file to IPFS:', err);
      alert('An error occurred during the upload. Please try again.');
    }
  };

  return (
    <div>
      <h2>Upload Campaign Files</h2>
      {/* Input for image */}
      <input type="file" onChange={handleImageChange} accept="image/*" />
      {/* Input for file */}
      <input type="file" onChange={handleFileChange} />
      {/* Input for campaign link */}
      <input
        type="text"
        placeholder="Campaign link"
        value={link}
        onChange={handleLinkChange}
      />
      {/* Button to upload files to IPFS */}
      <button onClick={uploadToIPFS}>Upload to IPFS</button>
    </div>
  );
};

export default UploadFile;
