import React, { useState, useEffect } from 'react';
import { create } from '@web3-storage/w3up-client';

const UploadFile = () => {
  const [file, setFile] = useState(null);
  const [status, setStatus] = useState('');
  const [client, setClient] = useState(null);

  // Predefined Space DID (set this to your actual Space DID if you have one)
  const spaceDid = 'did:key:your-space-did-here';  // Replace with your Space DID

  // Your Agent DID (Replace this with your actual Agent DID)
  const agentDid = 'did:key:your-agent-did-here';  // Replace with your Agent DID

  // Initialize Web3.Storage client when the component mounts
  useEffect(() => {
    const initializeClient = async () => {
      try {
        // Create the client instance with the provided Agent DID
        const newClient = await create({ agent: agentDid });

        // Set the predefined Space DID
        await newClient.setCurrentSpace(spaceDid);
        setClient(newClient); // Store the client instance
        setStatus('Client initialized and space set!');
      } catch (error) {
        console.error('Error initializing client:', error);
        setStatus('Error initializing client. Please try again.');
      }
    };

    initializeClient();
  }, []);

  // Handle file input change
  const handleFileChange = (event) => {
    setFile(event.target.files[0]);
  };

  // Upload file to Web3.Storage
  const uploadFile = async () => {
    if (!file || !client) {
      alert('Please select a file and make sure the client is initialized!');
      return;
    }

    setStatus('Uploading...');

    try {
      // Upload the file to Web3.Storage
      const fileCid = await client.uploadFile(file);
      console.log('File uploaded successfully! CID:', fileCid);

      setStatus(`File uploaded successfully! CID: ${fileCid}`);
    } catch (error) {
      console.error('Error uploading file:', error);
      setStatus('Error uploading file. Please try again.');
    }
  };

  return (
    <div>
      <h2>Upload a File to Web3.Storage</h2>

      {/* File input */}
      <input type="file" onChange={handleFileChange} />

      {/* Upload button */}
      <button onClick={uploadFile}>Upload</button>

      {/* Status message */}
      <p>{status}</p>
    </div>
  );
};

export default UploadFile;
