import React, { useState } from 'react';
import { create } from 'ipfs-http-client';
import { Buffer } from 'buffer';  // Import Buffer polyfill

// Infura project credentials
const projectId = '5275d3cba1cc8b5fb3fc';  // Replace with your Infura Project ID
const projectSecret = '3bed044d9789917ea9c04271df631325a40f5d5b4bfc90afc2b5e2a3d4fe8d9c';  // Replace with your Infura Project Secret

// Create the authentication header (Base64 encoding of Project ID and Secret)
const auth = 'Basic ' + Buffer.from(`${projectId}:${projectSecret}`).toString('base64');

// Create an IPFS client instance with Infura's endpoint
const ipfsClient = create({
  host: 'ipfs.infura.io',
  port: 5001,
  protocol: 'https',
  headers: {
    authorization: auth,
  },
});

const UploadFile = ({ onFileUploaded }) => {
  const [file, setFile] = useState(null);
  const [image, setImage] = useState(null);
  const [link, setLink] = useState('');

  const handleFileChange = (event) => {
    setFile(event.target.files[0]);
  };

  const handleImageChange = (event) => {
    setImage(event.target.files[0]);
  };

  const handleLinkChange = (event) => {
    setLink(event.target.value);
  };

  const uploadToIPFS = async () => {
    try {
      // Check if all fields are filled
      if (!file || !image || !link) {
        alert('Please fill in all fields!');
        return;
      }

      // Upload the image to IPFS
      const imageResponse = await ipfsClient.add(image);
      console.log('Image uploaded to IPFS with CID:', imageResponse.path);

      // Upload the file to IPFS
      const fileResponse = await ipfsClient.add(file);
      console.log('File uploaded to IPFS with CID:', fileResponse.path);

      // Pass the CIDs and link to the parent component
      onFileUploaded(imageResponse.path, fileResponse.path, link);
    } catch (err) {
      console.error('Error uploading to IPFS:', err);
      alert('An error occurred during the upload. Please try again.');
    }
  };

  return (
    <div>
      <h2>Upload Campaign Files</h2>
      {/* Input for image */}
      <input type="file" onChange={handleImageChange} accept="image/*" />
      {/* Input for file */}
      <input type="file" onChange={handleFileChange} />
      {/* Input for campaign link */}
      <input
        type="text"
        placeholder="Campaign link"
        value={link}
        onChange={handleLinkChange}
      />
      {/* Button to upload files to IPFS */}
      <button onClick={uploadToIPFS}>Upload to IPFS</button>
    </div>
  );
};

export default UploadFile;
