import React, { useState } from 'react';

const UploadFile = () => {
  // State variables
  const [file, setFile] = useState(null);
  const [uploading, setUploading] = useState(false);
  const [cid, setCid] = useState('');

  // Handle file input change
  const handleFileChange = (e) => {
    setFile(e.target.files[0]); // Capture the first file selected
  };

  // Handle file upload
  const handleUpload = async () => {
    if (!file) {
      alert('Please select a file first');
      return;
    }

    setUploading(true);

    const formData = new FormData();
    formData.append('file', file); // Append the selected file to FormData

    try {
      // Send file to the backend (replace with your backend URL)
      const response = await fetch('http://localhost:5000/upload', {
        method: 'POST',
        body: formData,
      });

      // Check if the response is ok
      if (response.ok) {
        const data = await response.json(); // Parse the response JSON
        setCid(data.cid); // Set the CID of the uploaded file
        alert(`File uploaded successfully! CID: ${data.cid}`);
      } else {
        const errorData = await response.json();
        alert(`Upload failed: ${errorData.message || 'Unknown error'}`);
      }
    } catch (error) {
      console.error('Error uploading file: ', error);
      alert('Upload failed');
    } finally {
      setUploading(false); // Reset the uploading state
    }
  };

  return (
    <div>
      <h1>Upload File to Web3.Storage</h1>

      {/* File input */}
      <input
        type="file"
        onChange={handleFileChange}
      />

      {/* Upload button */}
      <button
        onClick={handleUpload}
        disabled={uploading} // Disable button while uploading
      >
        {uploading ? 'Uploading...' : 'Upload File'}
      </button>

      {/* Display CID link if file is uploaded */}
      {cid && (
        <div>
          <p>File uploaded! View it on IPFS:</p>
          <a
            href={`https://${cid}.ipfs.w3s.link`}
            target="_blank"
            rel="noopener noreferrer"
          >
            View File
          </a>
        </div>
      )}
    </div>
  );
};

export default UploadFile;
