import React, { useState } from 'react';
import { create } from '@web3-storage/w3up-client';

const UploadFile = () => {
  const [file, setFile] = useState(null);
  const [status, setStatus] = useState('');
  const [client, setClient] = useState(null);
  const [spaceDid, setSpaceDid] = useState(''); // Space DID to set for current space

  // Create a Web3.Storage client
  const initializeClient = async () => {
    try {
      // Create the client instance without authentication
      const newClient = await create();

      // Optionally, set the Space DID if you know it
      await newClient.setCurrentSpace(spaceDid || 'did:key:...');
      setClient(newClient); // Store the client instance
      setStatus('Client initialized and space set!');
    } catch (error) {
      console.error('Error initializing client:', error);
      setStatus('Error initializing client. Please try again.');
    }
  };

  // Handle file input change
  const handleFileChange = (event) => {
    setFile(event.target.files[0]);
  };

  // Upload file to Web3.Storage
  const uploadFile = async () => {
    if (!file || !client) {
      alert('Please select a file and make sure the client is initialized!');
      return;
    }

    setStatus('Uploading...');

    try {
      // Upload the file to Web3.Storage
      const fileCid = await client.uploadFile(file);
      console.log('File uploaded successfully! CID:', fileCid);

      setStatus(`File uploaded successfully! CID: ${fileCid}`);
    } catch (error) {
      console.error('Error uploading file:', error);
      setStatus('Error uploading file. Please try again.');
    }
  };

  return (
    <div>
      <h2>Upload a File to Web3.Storage</h2>

      {/* Initialize client button */}
      <button onClick={initializeClient}>Initialize Client</button>

      {/* Space DID input */}
      <div>
        <input
          type="text"
          placeholder="Enter Space DID"
          value={spaceDid}
          onChange={(e) => setSpaceDid(e.target.value)}
        />
        <button onClick={() => setSpaceDid(spaceDid)}>Set Space DID</button>
      </div>

      {/* File input */}
      <input type="file" onChange={handleFileChange} />

      {/* Upload button */}
      <button onClick={uploadFile}>Upload</button>

      {/* Status message */}
      <p>{status}</p>
    </div>
  );
};

export default UploadFile;
