import React, { useEffect, useState } from 'react';
import { ethers } from 'ethers';
import campaignAbi from './Campaign.json';
import donationTokenAbi from './DonationToken.json';
import Login from './Login';
import './App.css';

function CampaignInteraction() {
    const [accountAddress, setAccountAddress] = useState('');
    const [accountBalance, setAccountBalance] = useState('');
    const [donationAmount, setDonationAmount] = useState('');
    const [targetAmount, setTargetAmount] = useState('');
    const [progress, setProgress] = useState(0);
    const [totalDonations, setTotalDonations] = useState(0);
    const [isTargetReached, setIsTargetReached] = useState(false);
    const [showThankYouPopup, setShowThankYouPopup] = useState(false);
    const [provider] = useState(new ethers.providers.JsonRpcProvider("http://localhost:8545"));
    const [campaignContract, setCampaignContract] = useState(null);
    const [isLoggedIn, setIsLoggedIn] = useState(false);

    useEffect(() => {
        async function initializeContract() {
            const signer = provider.getSigner();
            const campaignAddress = "0xe7f1725E7734CE288F8367e1Bb143E90bb3F0512";
            const campaign = new ethers.Contract(campaignAddress, campaignAbi.abi, signer);
            setCampaignContract(campaign);
        }
        initializeContract();
    }, [provider]);

    async function handleLogin(address) {
        setIsLoggedIn(true);
        setAccountAddress(address);
        const balance = await provider.getBalance(address);
        setAccountBalance(ethers.utils.formatEther(balance));
    }

    async function handleLogout() {
        setIsLoggedIn(false);
        setAccountAddress('');
        setAccountBalance('');
        setDonationAmount('');
        setProgress(0);
        setTargetAmount('');
        setTotalDonations(0);
    }

    async function donate() {
        if (!campaignContract || !donationAmount) {
            alert("Please enter a valid donation amount.");
            return;
        }
        try {
            const donationValue = ethers.utils.parseEther(donationAmount);
            const tx = await campaignContract.donate({ value: donationValue });
            await tx.wait();

            const total = await campaignContract.totalDonations();
            const target = await campaignContract.targetAmount();

            setTotalDonations(ethers.utils.formatEther(total));
            setTargetAmount(ethers.utils.formatEther(target));
            setProgress((parseFloat(total) / parseFloat(target)) * 100);

            if (parseFloat(total) >= parseFloat(target)) {
                setIsTargetReached(true);
            }

            setShowThankYouPopup(true);
        } catch (error) {
            alert("Error while donating: " + error.message);
        }
    }

    return (
        <div className="campaign-container">
            {!isLoggedIn ? (
                <Login onLogin={handleLogin} />
            ) : (
                <div>
                    <header className="campaign-header">
                        <h1>Support Our Campaign</h1>
                        <p>Join us in making a difference! Your donations help us achieve our goals and bring positive change to the community.</p>
                    </header>

                    <div className="account-info">
                        <h2>Account Information</h2>
                        <p>Connected Account: {accountAddress}</p>
                        <p>Balance: {accountBalance} ETH</p>
                        <button onClick={handleLogout}>Logout</button>
                    </div>

                    <div className="campaign-progress">
                        <h2>Campaign Progress</h2>
                        <p>Total Donations: {totalDonations} ETH</p>
                        {targetAmount && <p>Target Amount: {targetAmount} ETH</p>}
                        <p>Progress: {progress.toFixed(2)}%</p>
                        <progress value={progress} max="100"></progress>
                    </div>

                    <div className="donation-section">
                        <h2>Make a Donation</h2>
                        <label>Amount (ETH):</label>
                        <input
                            type="number"
                            value={donationAmount}
                            onChange={(e) => setDonationAmount(e.target.value)}
                        />
                        <button onClick={donate}>Donate</button>
                    </div>

                    {showThankYouPopup && (
                        <div className="popup">
                            <div className="popup-content">
                                <h2>Thank You!</h2>
                                <p>Your generosity makes a difference. Thank you for your support!</p>
                                <button onClick={() => setShowThankYouPopup(false)}>Close</button>
                            </div>
                        </div>
                    )}

                    {isTargetReached && (
                        <div className="celebration">
                            <h2>ðŸŽ‰ Target Reached! ðŸŽ‰</h2>
                            <p>We have reached our goal, thanks to your kindness and support!</p>
                        </div>
                    )}
                </div>
            )}
        </div>
    );
}

export default CampaignInteraction;
