import React, { useEffect, useState } from 'react';
import { ethers } from 'ethers';
import tokenAbi from "./RealEstateToken.json";
import marketplaceAbi from "./RealEstateMarketplace.json";
import Login from './Login'; // Import the Login component
import './App.css';

function RealEstateTokenInteraction() {
    const [tokenId, setTokenId] = useState('');
    const [accountAddress, setAccountAddress] = useState('');
    const [accountBalance, setAccountBalance] = useState('');
    const [pay, setpay] = useState('');
    const [tokenId1, setTokenId1] = useState('');
    const [location, setLocation] = useState('');
    const [price, setPrice] = useState('');
    const [provider] = useState(new ethers.providers.JsonRpcProvider("http://localhost:8545"));
    const [index,new1] = useState(""); // Get signer for issuing token // Get signer for buying tokens
   

 const [accountData, setAccountData] = useState([]);
    const [isLoggedIn, setIsLoggedIn] = useState(false); // Add state for login statusS


    useEffect(() => {
        // Fetch private keys from Hardhat network
        async function fetchAccountData() {
            try {

                const accounts = await provider.listAccounts();
                //console.log(accounts);

                // Create a dictionary with addresses and private keys
                const accountDictionary = []
                accounts.forEach(account => {
                    console.log(account)
                    accountDictionary.push(account)
                });

                setAccountData(accountDictionary);
            } catch (error) {
                console.error('Error fetching account data:', error);
            }
        }

        fetchAccountData();
        console.log(accountData);
    }, []);
    useEffect(() => {
        async function fetchAccountInfo() {
            try {
                const accounts = await provider.listAccounts();
                if (accounts.length > 0) {
                    const balance = await provider.getBalance(accounts[1]);
                    setAccountAddress(accounts[1]);
                    setAccountBalance(ethers.utils.formatEther(balance));
                    const signer = provider.getSigner(index);
                }
            } catch (error) {
                console.error('Error fetching account info:', error);
            }
        }

        fetchAccountInfo();
    }, []);

    async function issueToken() {
        try {
            if (tokenContract) {
                const issueTx = await tokenContract.issueToken(parseInt(tokenId), location, ethers.utils.parseEther(price));
                await issueTx.wait();
                console.log("Token Issued Successfully!");
            }
        } catch (error) {
            console.error('Error:', error);
        }
    }

    useEffect(() => {
        if (index !== null) {
            const signer = provider.getSigner(index);

            if (!tokenContract) {
                const tokenAddress = "0x5FbDB2315678afecb367f032d93F642f64180aa3";
                const token = new ethers.Contract(tokenAddress, tokenAbi.abi, signer);
                setTokenContract(token);
            }

            if (!marketplaceContract) {
                const marketplaceAddress = "0xe7f1725E7734CE288F8367e1Bb143E90bb3F0512";
                const marketplace = new ethers.Contract(marketplaceAddress, marketplaceAbi.abi, signer);
                setMarketplaceContract(marketplace);
            }
        }
    }, [index,tokenContract, marketplaceContract]);

    async function handleLogin(address) {
        setIsLoggedIn(true);
        setAccountAddress(address);
        console.log(address);
        const balance = await provider.getBalance(address);
        console.log(balance);
        setAccountBalance(ethers.utils.formatEther(balance));
        new1(accountData.indexOf(address));
    }

    async function buyToken() {
        try {
            if (marketplaceContract) {
                const payValue = ethers.utils.parseEther(pay);
                const buyTx = await marketplaceContract.buyToken(tokenId1, { value: payValue });
                await buyTx.wait();
                console.log("Token bought successfully!");

                const updatedBalance = await provider.getBalance(accountAddress);
                setAccountBalance(ethers.utils.formatEther(updatedBalance));
            }
        } catch (error) {
            console.error('Error:', error);
        }
    }

    return (
        <div>
            {!isLoggedIn ? ( // Render login component if not logged in
                <Login accountData={accountData} onLogin={handleLogin} />
            ) : (
                <div>
                    <h1>Real Estate Token Interaction</h1>

                    <div>
                        <h2>Connected Account Information</h2>
                        <p>Address: {accountAddress}</p>
                        <p>Balance: {accountBalance} ETH</p>
                    </div>

                    <h2>Request New Token</h2>
                    <label>Token ID:</label>
                    <input type="number" value={tokenId} onChange={(e) => setTokenId(e.target.value)} /><br />
                    <label>Location:</label>
                    <input type="text" value={location} onChange={(e) => setLocation(e.target.value)} /><br />
                    <label>Price (ETH):</label>
                    <input type="number" value={price} onChange={(e) => setPrice(e.target.value)} /><br />
                    <button onClick={issueToken}>Issue Token</button>

                    <hr />

                    <h2>Buy Token</h2>
                    <label>Token ID:</label>
                    <input type="number" value={tokenId1} onChange={(e) => setTokenId1(e.target.value)} /><br />
                    <label>How much will you pay:</label>
                    <input type="text" value={pay} onChange={(e) => setpay(e.target.value)} /><br />
                    <button onClick={buyToken}>Buy Token</button>
                </div>
            )}
        </div>
    );
}
export default RealEstateTokenInteraction;
