import React, { useState } from 'react';
import { ethers } from 'ethers';
import './App.css';

function RealEstateTokenInteraction() {
    const [tokenId, setTokenId] = useState('');
    const [location, setLocation] = useState('');
    const [price, setPrice] = useState('');
    const [recipientAddress, setRecipientAddress] = useState('');

    async function interact() {
        try {
            // Connect to your local network
            const provider = new ethers.providers.JsonRpcProvider("http://localhost:8545");
            const signer = provider.getSigner();

            // Replace with your deployed contract address
            const deployedTokenAddress = "0x5FbDB2315678afecb367f032d93F642f64180aa3";
            const token = new ethers.Contract(deployedTokenAddress, abi, signer);

            // Example: Call a view method
            const tokenInfo = await token.viewToken(tokenId);
            console.log("Token Info:", tokenInfo);

            // Example: Call a state-changing method (issueToken)
            const issueTx = await token.connect(signer).issueToken(parseInt(tokenId), location, ethers.utils.parseEther(price));
            await issueTx.wait();
            console.log("Token Issued Successfully!");

            // Example: Call a state-changing method (transferToken)
            const transferTx = await token.connect(signer).transferToken(recipientAddress, parseInt(tokenId));
            await transferTx.wait();
            console.log("Token Transferred Successfully!");

            // Update UI or handle success
        } catch (error) {
            // Handle errors
            console.error('Error:', error);
        }
    }

    return (
        <div>
            <h1>Real Estate Token Interaction</h1>

            <h2>Issue New Token</h2>
            <label>Token ID:</label>
            <input type="number" value={tokenId} onChange={(e) => setTokenId(e.target.value)} /><br />
            <label>Location:</label>
            <input type="text" value={location} onChange={(e) => setLocation(e.target.value)} /><br />
            <label>Price (ETH):</label>
            <input type="number" value={price} onChange={(e) => setPrice(e.target.value)} /><br />
            <button onClick={interact}>Issue Token</button>

            <hr />

            <h2>Transfer Token</h2>
            <label>Token ID:</label>
            <input type="number" value={tokenId} onChange={(e) => setTokenId(e.target.value)} /><br />
            <label>Recipient Address:</label>
            <input type="text" value={recipientAddress} onChange={(e) => setRecipientAddress(e.target.value)} /><br />
            <button onClick={interact}>Transfer Token</button>
        </div>
    );
}

export default RealEstateTokenInteraction;
