import React, { useState } from 'react';
import { create } from '@web3-storage/w3up-client';

const FileUpload = () => {
  const [file, setFile] = useState(null);
  const [status, setStatus] = useState('');
  const [cid, setCid] = useState('');
  const [email, setEmail] = useState('');
  const [isLoggedIn, setIsLoggedIn] = useState(false);

  const handleFileChange = (event) => {
    setFile(event.target.files[0]);
  };

  const initializeClient = async () => {
    try {
      setStatus('Initializing client...');
      const client = await create();

      if (email) {
        await client.login(email); // Log in using email
        setIsLoggedIn(true);
        setStatus('Logged in successfully!');

        // Set the current space using the provided DID
        const spaceDid = 'z6MkkipjA1gWSEpAerXN91c1nwqjfeeJ5aetK8hpfYVwyKgy'; // Replace with your actual DID
        await client.setCurrentSpace(spaceDid);

        setStatus(`Space ${spaceDid} is set and ready for use!`);
      } else {
        setStatus('Please provide a valid email to login.');
      }
    } catch (error) {
      console.error('Error initializing client:', error);
      setStatus('Error initializing client');
    }
  };

  const uploadFile = async () => {
    if (!file) {
      setStatus('Please select a file to upload.');
      return;
    }

    try {
      setStatus('Uploading file...');
      const client = await create(); // Initialize Web3.Storage client

      // Use the same space DID for file upload
      const spaceDid = 'did:key:your-space-did-here'; // Replace with your actual DID
      await client.setCurrentSpace(spaceDid); // Set the current space

      // Upload the file to Web3.Storage
      const fileCid = await client.uploadFile(file);
      setCid(fileCid); // Store the CID of the uploaded file

      setStatus(`File uploaded successfully! CID: ${fileCid}`);
    } catch (error) {
      console.error('Error uploading file:', error);
      setStatus('Error uploading file');
    }
  };

  return (
    <div>
      <h2>Upload File to Web3.Storage</h2>

      <div>
        <input
          type="email"
          placeholder="Enter your email"
          value={email}
          onChange={(e) => setEmail(e.target.value)}
        />
        <button onClick={initializeClient}>Initialize and Log In</button>
        {isLoggedIn && <p>Status: Logged in successfully</p>}
      </div>

      <div>
        <input type="file" onChange={handleFileChange} />
        <button onClick={uploadFile}>Upload File</button>
      </div>

      <p>{status}</p>
      {cid && <p>File CID: {cid}</p>}
    </div>
  );
};

export default FileUpload;
