import React, { useState } from 'react';

function Login({ accountData, onLogin }) {
    const [address, setAddress] = useState('');
    const [password, setPassword] = useState('');
    const [error, setError] = useState('');



   function handleLogin() {
        if (!address || !password) {
            setError('Please enter both address and password.');
            return;
        }

        // Check if address exists in accountData
        if (accountData[address]) {
            const privateKey = accountData[address];
            // Check if password matches the private key
            if (password === privateKey) {
                console.log(address);
                // Login successful, pass address to parent component
                onLogin(address);
            } else {
                setError('Incorrect password.');
            }
        } else {
            setError('Address not found.');
        }
    }

    return (
        <div>
            <h2>Login</h2>
            <div>
                <label>Address:</label>
                <input type="text" value={address} onChange={(e) => setAddress(e.target.value)} />
            </div>
            <div>
                <label>Password:</label>
                <input type="password" value={password} onChange={(e) => setPassword(e.target.value)} />
            </div>
            <button onClick={handleLogin}>Login</button>
            {error && <p style={{ color: 'red' }}>{error}</p>}
        </div>
    );
}

export default Login;
